    public function getSourceContext()
    {
        return new Source("{% extends 'base-back.html.twig' %}
{% block title %}Edit User{% endblock %}
{% block stylesheets %}
    {{ parent() }}
    <style>
        input.form-control {
            border: none;
            border-bottom: 1px solid #ccc;
            box-shadow: none;
            border-radius: 0;
        }
    </style>
{% endblock %}
{% block body %}
    {{ parent() }}
    <style>
        .form-group {
            margin-bottom: 15px;
            width: 700px; /* Adjust as needed */
            height: 60px; /* Adjust as needed */
        }

        .btn {
            padding: 15px 32px;
            text-align: center;
            text-decoration: none;
            display: inline-block;
            font-size: 16px;
            margin: 4px 2px;
            cursor: pointer;
        }
    </style>

    <div class=\"card\" style=\"width: 50rem; margin-left: 500px; margin-top: 100px\">
        <div class=\"card-body\">
            <h5 class=\"card-title\">Edit User</h5>
            {{ form_start(form) }}
            <div class=\"form-group\">
                {{ form_label(form.email) }}
                {{ form_widget(form.email, {'attr': {'class': 'form-control'}}) }}
                {{ form_errors(form.email) }}
            </div>
            <div class=\"form-group\">
                {{ form_label(form.roles) }}
                {{ form_widget(form.roles, {'attr': {'class': 'form-control'}}) }}
                {{ form_errors(form.roles) }}
            </div>
            <div class=\"form-group\">
                {{ form_label(form.isVerified) }}
                {{ form_widget(form.isVerified, {'attr': {'class': 'form-control'}}) }}
                {{ form_errors(form.isVerified) }}
            </div>
            <br>
            <button type=\"submit\" class=\"btn btn-primary\">Update</button>
            <a href=\"{{ path('app_user_index') }}\" class=\"btn btn-secondary\">Back to List</a>
            {{ form_end(form) }}
        </div>
    </div>
{% endblock %}", "user/edit.html.twig", "C:\\Users\\touti\\Documents\\PidevWeb\\templates\\user\\edit.html.twig");
