    public function getSourceContext()
    {
        return new Source("{% extends 'base-back.html.twig' %}

{% block title %}Create new User{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <style>
        .form-group {
            margin-bottom: 15px;
            width: 700px; /* Adjust as needed */
            height: 60px; /* Adjust as needed */
        }

        input.form-control {
            border: none;
            border-bottom: 1px solid #ccc;
            box-shadow: none;
            border-radius: 0;
            width: 100%; /* Adjust as needed */
        }

        .btn {
            padding: 15px 32px;
            text-align: center;
            text-decoration: none;
            display: inline-block;
            font-size: 16px;
            margin: 4px 2px;
            cursor: pointer;
        }
    </style>
{% endblock %}

{% block body %}
    {{ parent() }}
    <div class=\"card\" style=\"width: 50rem; margin-left: 500px; margin-top: 100px\">
        <div class=\"card-body\">
            <h5 class=\"card-title\">Create new User</h5>
            {{ form_start(form) }}
            <div class=\"form-group\">
                {{ form_label(form.email) }}
                {{ form_widget(form.email, {'attr': {'class': 'form-control'}}) }}
                {{ form_errors(form.email) }}
            </div>
            <div class=\"form-group\">
                {{ form_label(form.roles) }}
                {{ form_widget(form.roles, {'attr': {'class': 'form-control'}}) }}
                {{ form_errors(form.roles) }}
            </div>
            <div class=\"form-group\">
                {{ form_label(form.isVerified) }}
                {{ form_widget(form.isVerified, {'attr': {'class': 'form-control'}}) }}
                {{ form_errors(form.isVerified) }}
            </div>
            <button class=\"btn btn-primary\">Save</button>
            <a href=\"{{ path('app_user_index') }}\" class=\"btn btn-secondary\">Back to List</a>
            {{ form_end(form) }}
        </div>
    </div>
{% endblock %}", "user/new.html.twig", "C:\\Users\\touti\\Documents\\PidevWeb\\templates\\user\\new.html.twig");
